package com.syntax.class02;

public class doubleComparision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double a = 3000;
		double b = 2000;
		if (a > b) {
			System.out.println("Double value is larger than " + b);
		} else
			System.out.println("Double value is smaller than " + b);

	}

}
